INSERT INTO Message VALUES('e88acd92-a420-47a7-8596-283cf026e32b','bee266e4-3e47-4f67-9991-ba44d39f8c2c','TEXT',replace('\n\nДля лечения запора :\n\nФорлакс 1 пакетик (4 грамма) развести его нужно в 50 мл воды/еды, предлагать утром, для того чтобы дефекация была во 2 половине дня. Форлакс возвращает из организма воду обратно в кишечник, тем самым размягчая кал. Дефекация становится безболезненной, уходит натуживание и сдерживание. \nЦель лечения - получить разжиженный стул, как в первые месяцы жизни.\nКурс - 1-3 мес, т е 1 мес принимаете 4г ежедневно, затем идёт постепенное снижение дозы по 1г в нед.\n\nТаким образом на 5 нед лечения 3 г в ежедневно, на 6 нед 2г ежедневно и тд.\nОтменять резко нельзя. ','\n',char(10)),'active',1745932415602,6);
