INSERT INTO Message VALUES('2bfb68ab-4be5-43a2-8070-09ac136d9849','9f3d800c-3de4-4abc-9b49-94bd463657e4','TEXT',replace('Добрый день! Это снова мы, Волков Тимофей))\nЧто беспокоит: ночью услышала что сопит, хрюкает, утром высосала сопельки, они были зеленого цвета. \nКашля, температуры нет.\nЕще снова стал гноиться глаз, 23 го были записаны на зондирование, кровь на свертываемость плохая. Вчера сдали кровь на коалуограмму, 7 го мая едем к гематологу.\nОдин день глаз так заплыл гноем, что не откосы с утра его. Началось это с 25 го.\nС пн начала капать тобрекс, стало лучше.','\n',char(10)),'active',1745994013740,NULL);
